#!/usr/bin/env python3
#R is A or G
#Y is T or C
import re
import sys
#line counter
# length line
#change regular expression
# plus one
# enumerate motifs
#(r”[A/G]G[G/T]T[C/G]A.....[A/G]G[G/T]T[C/G]A“)
motif=r"([A|G]G[G|T]T[C|G]A.....[A|G]G[G|T]T[C|G]A)"
dna = {'chromosome1':'','chromosome2':''}
with open ('motif_hit_out.txt','w') as output:
  output.write('#chr\tmotif\tstart\tend\n')
  for chromosome in dna:
    for i in re.finditer(motif,dna[chromosome]):
      find1=i.group(0)
      start1=i.start(0) + 1
      end1=i.end(0)
      output.write(f'{chromosome}:\t{find1}\t{start1}\t{end1}\n')

